function calculateCommission(salesAmount, leaveDays, cashReceivedPercent) {
  if (salesAmount < 0) {
    return "销售额不能为负数";
  }
  if (leaveDays < 0) {
    return "请假天数不能为负数";
  }
  if (cashReceivedPercent < 0 || cashReceivedPercent > 100) {
    return "现金到账百分比应在0~100之间";
  }
  if (salesAmount > 2000000 && leaveDays <= 10) {
    if (cashReceivedPercent >= 60) {
      return `佣金为：${(salesAmount / 7).toFixed(2)} 元`;
    } else {
      return "现金到帐不足60%，不计算佣金";
    }
  } else {
    if (cashReceivedPercent <= 85) {
      return `佣金为：${(salesAmount / 6).toFixed(2)} 元`;
    } else {
      return `佣金为：${(salesAmount / 5).toFixed(2)} 元`;
    }
  }
}

