function calculateSalesCommission(hosts, displays, peripherals) {
  if (hosts === -1) {
    return "输入为结束信号：无需计算销售额与佣金。";
  }

  // 最大数量限制
  if (
    hosts < 1 || hosts > 70 ||
    displays < 1 || displays > 80 ||
    peripherals < 1 || peripherals > 90
  ) {
    return "输入错误：某个组件数量超出限制范围或未能组成至少一台完整电脑。";
  }

  // 计算总销售额
  const totalSales =
    hosts * 25 +
    displays * 30 +
    peripherals * 45;

  // 计算佣金
  let commissionRate = 0.1;
  if (totalSales > 1800) {
    commissionRate = 0.2;
  } else if (totalSales > 1000) {
    commissionRate = 0.15;
  }
  const commission = totalSales * commissionRate;

  return `销售总额为 ¥${totalSales}，佣金为 ¥${commission.toFixed(2)}（提成比例：${commissionRate * 100}%）`;
}
