function triangleJudge(a, b, c) {
  if (a < 1 || a > 200 || b < 1 || b > 200 || c < 1 || c > 200) {
    return "输入超出范围";
  }
  if (a + b <= c || a + c <= b || b + c <= a) {
    return "不构成三角形";
  }
  if (a === b && b === c) {
    return "等边三角形";
  } else if (a === b || a === c || b === c) {
    return "等腰三角形";
  } else {
    return "一般三角形";
  }
}

function triangleJudge(a, b, c) {
  // 错误版本：条件判断顺序不合理（先判断等腰再判断等边）造成判断错误
  if (a < 1 || a > 200 || b < 1 || b > 200 || c < 1 || c > 200) {
    return "输入超出范围";
  }
  if (a + b <= c || a + c <= b || b + c <= a) {
    return "不构成三角形";
  }
  if (a === b || a === c || b === c) {
    return "等腰三角形";
  } else if (a === b && b === c) {
    return "等边三角形";
  } else {
    return "一般三角形";
  }
}
