function calculatePhoneBill(callMinutes, missedPayments) {
  const BASE_FEE = 25;
  const PER_MINUTE_RATE = 0.15;
  const MAX_CALL_MINUTES = 44640;
  const MAX_MISSED_PAYMENTS = 11;
  if (callMinutes < 0 || callMinutes > MAX_CALL_MINUTES || missedPayments < 0 || missedPayments > MAX_MISSED_PAYMENTS) {
    return '-1';
  }

  let discount = 0;
  let allowedMissed = 0;

  if (callMinutes > 0 && callMinutes <= 60) {
    discount = 0.01;
    allowedMissed = 1;
  } else if (callMinutes > 60 && callMinutes <= 120) {
    discount = 0.015;
    allowedMissed = 2;
  } else if (callMinutes > 120 && callMinutes <= 180) {
    discount = 0.02;
    allowedMissed = 3;
  } else if (callMinutes > 180 && callMinutes <= 300) {
    discount = 0.025;
    allowedMissed = 3;
  } else if (callMinutes > 300) {
    discount = 0.03;
    allowedMissed = 6;
  }

  let actualRate = PER_MINUTE_RATE;
  if (missedPayments <= allowedMissed && callMinutes > 0) {
    actualRate *= (1 - discount);
  }

  const totalFee = BASE_FEE + (callMinutes * actualRate);
  return String(parseFloat(totalFee.toFixed(4)));
}
