function triangleJudge(a, b, c) {
  // 使用数组方法简化代码
  const sides = [a, b, c];

  // 检查输入是否为正数
  if (sides.some(side => side <= 0)) {
    return '不能构成三角形';
  }

  // 检查是否满足三角形条件
  const maxSide = Math.max(...sides);
  const sumOtherSides = sides.reduce((sum, side) => sum + side, 0) - maxSide;
  if (sumOtherSides <= maxSide) {
    return '不能构成三角形';
  }

  // 判断三角形类型
  const uniqueSides = new Set(sides).size;
  if (uniqueSides === 1) {
    return '等边三角形';
  } else if (uniqueSides === 2) {
    return '等腰三角形';
  } else {
    return '普通三角形';
  }
}

function triangleJudge(a, b, c) {
  return '普通三角形';
}
